import styled from 'styled-components';

export const SoundsContentsList = styled.div`
  margin: auto;
  display: flex;
  justify-content: space-around;
  flex-wrap: wrap;
  height: 30%;
  width: 70%;
`;

export const Box = styled.div`
  padding: 0.5em 1em;
  margin: 2em 0;
  color: #777777;
  background: white;
  border-top: solid 5px #777777;
  box-shadow: 0 3px 5px rgba(0, 0, 0, 0.22);
`;

export const Under = styled.h2 `
  border-bottom: solid 2px gray;
  font-family:inherit;
  font-size: 35px;
  font-weight: 700;
  word-break: break-all;
  color: #777777;
`;

export const PostButton = styled.div`
  color: skyblue;
  text-decoration:none;
  float: right;
  padding:15px;
  border-radius: 100vh;
  background-color: #A9A9A9;`
